package com.presentation.app.dealsnest.receiver;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import com.presentation.app.dealsnest.ui.NoInternetActivity;import com.presentation.app.dealsnest.utils.CommonUtils;public class NetworkChangeReceiver extends BroadcastReceiver {	@Override	public void onReceive(Context context, Intent intent) {		if ("android.net.conn.CONNECTIVITY_CHANGE".equals(intent.getAction())) {			if (!CommonUtils.isNetworkAvailable(context)) {				Intent noInternetIntent = NoInternetActivity.getActivityIntent(context);				context.startActivity(noInternetIntent);			}		}	}}